import { CreateAlertResponse, CreateQuestionResponse, DesktopNotifPartial, GetCourseOverridesResponse, GetCourseResponse, GetProfileResponse, GetQuestionResponse, GetQueueResponse, ListQuestionsResponse, UpdateCourseOverrideResponse, UpdateQuestionResponse } from "@koh/common";
import Axios from "axios";
import { plainToClass } from "class-transformer";

class APIClient {
  /**
   * Send HTTP and return data, optionally serialized with class-transformer (helpful for Date serialization)
   * @param method HTTP method
   * @param url URL to send req to
   * @param responseClass Class with class-transformer decorators to serialize response to
   * @param body body to send with req
   */
  async req(method, url, responseClass, body) {
    const res = (await this.axios.request({
      method,
      url,
      data: body
    })).data;
    return responseClass ? plainToClass(responseClass, res) : res;
  }

  constructor(baseURL = "") {
    this.axios = void 0;
    this.profile = {
      index: async () => this.req("GET", `/api/v1/profile`, GetProfileResponse),
      patch: async body => this.req("PATCH", `/api/v1/profile`, undefined, body),
      deleteProfilePicture: async () => this.req("DELETE", `/api/v1/profile/delete_profile_picture`)
    };
    this.course = {
      get: async courseId => this.req("GET", `/api/v1/courses/${courseId}`, GetCourseResponse),
      updateCalendar: async courseId => this.req("POST", `/api/v1/courses/${courseId}/update_calendar`),
      getCourseOverrides: async courseId => this.req("GET", `/api/v1/courses/${courseId}/course_override`, GetCourseOverridesResponse),
      addOverride: async (courseId, params) => this.req("POST", `/api/v1/courses/${courseId}/update_override`, UpdateCourseOverrideResponse, params),
      deleteOverride: async (courseId, params) => this.req("DELETE", `/api/v1/courses/${courseId}/update_override`, undefined, params)
    };
    this.taStatus = {
      checkIn: async (courseId, room) => this.req("POST", `/api/v1/courses/${courseId}/ta_location/${room}`),
      checkOut: async (courseId, room) => this.req("DELETE", `/api/v1/courses/${courseId}/ta_location/${room}`)
    };
    this.questions = {
      index: async queueId => this.req("GET", `/api/v1/queues/${queueId}/questions`, ListQuestionsResponse),
      create: async params => this.req("POST", `/api/v1/questions`, CreateQuestionResponse, params),
      get: async questionId => this.req("GET", `/api/v1/questions/${questionId}`, GetQuestionResponse),
      update: async (questionId, params) => this.req("PATCH", `/api/v1/questions/${questionId}`, UpdateQuestionResponse, params),
      notify: async questionId => this.req("POST", `/api/v1/questions/${questionId}/notify`)
    };
    this.queues = {
      get: async queueId => this.req("GET", `/api/v1/queues/${queueId}`, GetQueueResponse),
      update: async (queueId, params) => this.req("PATCH", `/api/v1/queues/${queueId}`, UpdateQuestionResponse, params),
      clean: async queueId => this.req("POST", `/api/v1/queues/${queueId}/clean`)
    };
    this.notif = {
      desktop: {
        credentials: async () => this.req("GET", "/api/v1/notifications/desktop/credentials"),
        register: async payload => this.req("POST", `/api/v1/notifications/desktop/device`, DesktopNotifPartial, payload),
        unregister: async deviceId => this.req("DELETE", `/api/v1/notifications/desktop/device/${deviceId}`, undefined)
      }
    };
    this.seeds = {
      delete: async () => this.req("GET", `/api/v1/seeds/delete`),
      create: async () => this.req("GET", `/api/v1/seeds/create`),
      fillQueue: async () => this.req("GET", `/api/v1/seeds/fill_queue`)
    };
    this.releaseNotes = {
      get: async () => this.req("GET", `/api/v1/release_notes`)
    };
    this.alerts = {
      get: async courseId => this.req("GET", `/api/v1/alerts/${courseId}`),
      create: async params => this.req("POST", `/api/v1/alerts`, CreateAlertResponse, params),
      close: async alertId => {
        this.req("PATCH", `/api/v1/alerts/${alertId}`);
      }
    };
    this.axios = Axios.create({
      baseURL: baseURL
    });
  }

}

export const API = new APIClient(process.env.NEXT_PUBLIC_API_URL);
